	//https://github.com/zenozeng/color-hash
	public static Color getHashColor(String toHash) {
		int hash = toHash.hashCode();
		float h = (hash % 359) / 255F; //prime
		float s = SAT_BRI_ARRAY[(Math.abs(hash/360) % SAT_BRI_ARRAY.length)];
		
		float v = SAT_BRI_ARRAY[(Math.abs(hash/360) / SAT_BRI_ARRAY.length % SAT_BRI_ARRAY.length)];
		
		return Color.getHSBColor(h, s, v);
	}
	
	public static List<String> getSymptoms(String toHash) {
		List<String> toReturn = new ArrayList<String>();
		int hash = toHash.hashCode();
		
		int amount = (Math.abs(hash/360) % 10) + 1;
		
		//System.out.println("'" + toHash + "' " + amount);
		
		for(int i = 0; i < amount; i++) {
			int picked = (Math.abs(hash/360) % SYMPTOMS.length / (i + 1));
			toReturn.add(SYMPTOMS[picked]/* + "  - " + picked + " / " + (SYMPTOMS.length - 1) */);
		}
		
		
		return toReturn;
	}
	
